import React, { useState, useEffect } from 'react';
import { KanbanBoard } from './KanbanBoard';
import App1 from './App1';
import App2 from './App2';
import { AiFillCaretDown } from 'react-icons/ai'; // Importar o ícone do menu hamburguer
import './App.css';
import './index.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('kanban');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // Estado para controlar a abertura do menu

  useEffect(() => {
    setLoading(true);
    setError(null);

    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [activeTab]);

  const renderContent = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading content: {error}</p>;

    switch (activeTab) {
      case 'kanban':
        return <KanbanBoard />;
      case 'app1':
        return <App1 />;
      case 'app2':
        return <App2 />;
      default:
        return null;
    }
  };

  return (
    <div className="text-center">
      <header className="mb-4 bg-purple-900 pt-6 rounded-b-3xl w-full">
        {/* Menu hamburguer */}
        <div className="flex justify-between items-center px-4 sm:px-6">
          <AiFillCaretDown
            className="text-white cursor-pointer block sm:hidden"
            size={30}
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          />
          <h1 className="text-5xl font-bold text-left mb-8 text-white">Aplicativos <br></br>React</h1>
        </div>

        {/* Menu de navegação */}
        <nav className={`pb-6 ${isMenuOpen ? 'block' : 'hidden'} sm:block`}>
          <button
            className={`mx-2 px-6 py-3 rounded-full ${
              activeTab === 'kanban' ? 'bg-rose-500 text-white' : 'bg-white'
            }`}
            onClick={() => {
              setActiveTab('kanban');
              setIsMenuOpen(false); // Fechar o menu ao clicar em um item
            }}
          >
            To Do List
          </button>
          <button
            className={`mx-2 px-6 py-3 rounded-full ${
              activeTab === 'app1' ? 'bg-rose-500 text-white' : 'bg-white'
            }`}
            onClick={() => {
              setActiveTab('app1');
              setIsMenuOpen(false); // Fechar o menu ao clicar em um item
            }}
          >
            Text Write Speed
          </button>
          <button
            className={`mx-2 px-6 py-3 rounded-full ${
              activeTab === 'app2' ? 'bg-rose-500 text-white' : 'bg-white'
            }`}
            onClick={() => {
              setActiveTab('app2');
              setIsMenuOpen(false); // Fechar o menu ao clicar em um item
            }}
          >
            Cookie Cliker
          </button>
        </nav>
      </header>
      <main>{renderContent()}</main>
    </div>
  );
};

export default App;
