import React, { useState, useEffect } from 'react';
import { KanbanBoard } from './KanbanBoard';
import App1 from './App1';
import App2 from './App2';
import './App.css';
import './index.css'

const App = () => {
  const [activeTab, setActiveTab] = useState('kanban');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [activeTab]);

  const renderContent = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading content: {error}</p>;

    switch (activeTab) {
      case 'kanban':
        return <KanbanBoard />;
      case 'app1':
        return <App1 />;
      case 'app2':
        return <App2 />;
      default:
        return null;
    }
  };

  return (
    <div className="text-center pt-6">
      <header className="mb-4 bg-slate-600 w-full">
        <h1 className="text-5xl font-bold mb-8">Aplicativos React</h1>
        <nav>
          <button
            className={`mx-2 px-4 py-2 rounded ${
              activeTab === 'kanban' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
            onClick={() => setActiveTab('kanban')}
          >
            Kanban Board
          </button>
          <button
            className={`mx-2 px-4 py-2 rounded ${
              activeTab === 'app1' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
            onClick={() => setActiveTab('app1')}
          >
            App 1
          </button>
          <button
            className={`mx-2 px-4 py-2 rounded ${
              activeTab === 'app2' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
            onClick={() => setActiveTab('app2')}
          >
            App 2
          </button>
        </nav>
      </header>
      <main>{renderContent()}</main>
    </div>
  );
};

export default App;
