import React, { useState, useEffect, useRef } from 'react';

const App1 = () => {

    const CookieClicker = () => {
        const [clicks, setClicks] = useState(0);
        const [cps, setCps] = useState(0);
        const [scale, setScale] = useState(1);

        const intervalRef = useRef(null);
        const lastClicksRef = useRef(clicks);

        // Atualiza CPS a cada segundo
        useEffect(() => {
            intervalRef.current = setInterval(() => {
                setCps(clicks - lastClicksRef.current);
                lastClicksRef.current = clicks;
            }, 1000);

            return () => {
                clearInterval(intervalRef.current);
            };
        }, [clicks]);

        // Função para lidar com o clique no cookie
        const handleClick = () => {
            setClicks(clicks + 1);
            // Animação de escala no cookie
            setScale(0.8);
            setTimeout(() => {
                setScale(1);
            }, 100);
        };

        return (
            <div className="flex flex-col items-center mt-8">
                <div className="relative" onClick={handleClick}>
                    <img
                        src="./Cookie.webp"
                        alt="Cookie"
                        style={{ transform: `scale(${scale})`, transition: 'transform 0.1s ease-out' }}
                        className="cursor-pointer"
                    />
                </div>
                <div className="mt-4">
                    <p className="text-lg">Cookies clicados: {clicks}</p>
                    <p className="text-lg">Cliques por segundo (CPS): {cps}</p>
                </div>
            </div>
        );
    };    
};      


export default App1;
