import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { KanbanBoard } from './KanbanBoard';
import App1 from './App1';
import App2 from './App2';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('kanban');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    // Simulando uma chamada AJAX com setTimeout
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [activeTab]);

  const renderContent = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error loading content: {error}</p>;

    switch (activeTab) {
      case 'kanban':
        return <KanbanBoard />;
      case 'app1':
        return <App1 />;
      case 'app2':
        return <App2 />;
      default:
        return null;
    }
  };

  return (
    <AppContainer>
      <header className="App-header">
        <h1>React Applications</h1>
        <nav>
          <TabButton onClick={() => setActiveTab('kanban')}>Kanban Board</TabButton>
          <TabButton onClick={() => setActiveTab('app1')}>App 1</TabButton>
          <TabButton onClick={() => setActiveTab('app2')}>App 2</TabButton>
        </nav>
      </header>
      <main>
        {renderContent()}
      </main>
    </AppContainer>
  );
};

export default App;

const AppContainer = styled.div`
  text-align: center;
  padding: 20px;
`;

const TabButton = styled.button`
  margin: 0 10px;
  padding: 10px;
  cursor: pointer;
  background-color: #61dafb;
  border: none;
  border-radius: 5px;
  color: white;

  &:hover {
    background-color: #21a1f1;
  }
`;
