import React from 'react';
import { AiOutlineDelete, AiOutlineArrowRight, AiOutlineArrowLeft, AiOutlineArrowDown, AiOutlineArrowUp } from 'react-icons/ai';

export const Task = ({ task, columnId, removeTask, moveTask }) => {
    const handleRemove = () => {
        removeTask(columnId, task.id);
    };

    const handleMoveRight = () => {
        if (columnId === 'todo') moveTask(columnId, 'inProgress', task.id);
        if (columnId === 'inProgress') moveTask(columnId, 'done', task.id);
    };

    const handleMoveLeft = () => {
        if (columnId === 'done') moveTask(columnId, 'inProgress', task.id);
        if (columnId === 'inProgress') moveTask(columnId, 'todo', task.id);
    };

    const isLastColumn = columnId === 'done'; // Verifica se está na última coluna

    return (
        <div className={`rounded-lg p-2 mb-2 flex justify-between items-center ${isLastColumn ? 'bg-gray-200' : 'bg-white'}`}>
            <div className="flex-1">{task.content}</div>
            <div className="flex gap-2">
                {/* Botões de movimento */}
                <button
                    onClick={handleMoveLeft}
                    disabled={columnId === 'todo'}
                    className={`rounded-md p-2 bg-gray-300 ${columnId === 'todo' || isLastColumn ? 'cursor-not-allowed bg-gray-400' : 'hover:bg-gray-400'} sm:hidden`}
                >
                    {isLastColumn ? <AiOutlineArrowUp /> : <AiOutlineArrowLeft />}
                </button>
                <button
                    onClick={handleMoveRight}
                    disabled={columnId === 'done'}
                    className={`rounded-md p-2 bg-gray-300 ${columnId === 'done' || isLastColumn ? 'cursor-not-allowed bg-gray-400' : 'hover:bg-gray-400'} sm:hidden`}
                >
                    {isLastColumn ? <AiOutlineArrowDown /> : <AiOutlineArrowRight />}
                </button>

                <button
                    onClick={handleMoveLeft}
                    disabled={columnId === 'todo'}
                    className={`rounded-md p-2 bg-gray-300 ${columnId === 'todo' || isLastColumn ? 'cursor-not-allowed bg-gray-400' : 'hover:bg-gray-400'} hidden sm:flex`}
                >
                    {isLastColumn ? <AiOutlineArrowUp /> : <AiOutlineArrowLeft />}
                </button>
                <button
                    onClick={handleMoveRight}
                    disabled={columnId === 'done'}
                    className={`rounded-md p-2 bg-gray-300 ${columnId === 'done' || isLastColumn ? 'cursor-not-allowed bg-gray-400' : 'hover:bg-gray-400'} hidden sm:flex`}
                >
                    {isLastColumn ? <AiOutlineArrowDown /> : <AiOutlineArrowRight />}
                </button>

                {/* Botão de remoção */}
                <button
                    onClick={handleRemove}
                    className={`rounded-md p-2 bg-red-300 hover:bg-red-400 ${isLastColumn ? 'bg-gray-200' : ''}`}
                >
                    <AiOutlineDelete />
                </button>
            </div>
        </div>
    );
};
