import React, { useState } from 'react';
import './App.css'; // Importe seu arquivo de estilo CSS aqui

const CookieClicker = () => {
    const [clicks, setClicks] = useState(0);
    const [scale, setScale] = useState(1);

    // Função para lidar com o clique no cookie
    const handleClick = () => {
        setClicks(clicks + 1);
        // Animação de escala no cookie
        setScale(0.9);
        setTimeout(() => {
            setScale(1);
        }, 100);
    };

    return (
        <div className="flex flex-col items-center mt-8">
            <div className="relative w-96" onClick={handleClick}>
<div className="relative w-96" onClick={handleClick}>
    <img
        src="/Cookie.webp"
        alt="Cookie"
        style={{
            transition: 'transform 0.1s ease-out',
            animation: 'rotate 10s linear infinite'
        }}
        className={`cursor-pointer transform scale-${scale}`}
    />
</div>

            </div>
            <div className="mt-4">
                <p className="text-lg">Cookies clicados: {clicks}</p>
            </div>
        </div>
    );
};

export default CookieClicker;
