import React, { useState } from 'react';

const TextAreaMirror = () => {
    const [text, setText] = useState('');

    const handleChange = (event) => {
        setText(event.target.value);
    };

    return (
        <div className="flex flex-col items-center mt-8">
            <textarea
                value={text}
                onChange={handleChange}
                rows="10"
                className="w-4/5 caret-pink-500 p-4 border border-purple-700 rounded-xl focus:border-purple-800"
                placeholder="Digite aqui..."
            />
            <div className="relative mt-4 w-full">
                <div className="absolute bottom-0 right-0 bg-white p-2 text-sm border border-gray-300 rounded-tl-lg">
                    {text.length} caracteres
                </div>
            </div>
            <div className="mt-4 w-full">
                <h3 className="text-lg font-bold">Texto digitado:</h3>
                <p className="mt-2 p-4 border border-gray-300 rounded-lg">{text}</p>
            </div>
        </div>
    );
};

export default TextAreaMirror;
